import { useState } from "react";
import { motion, useAnimation, useDragControls } from "framer-motion";
import styled from "styled-components";
import DriveNav from "@/components/_common/driveNav/DriveNav.tsx";
import MusicPlayer from "@/components/drive/musicPlayer/MusicPlayer.tsx";

const tracks = [
  {
    title: "Devil In A New Dress",
    artist: "Kanye West",
    albumCover:
      "data:image/jpeg;base64,",
  },
  {
    title: "Track 2",
    artist: "Artist 2",
    albumCover:
      "data:image/jpeg;base64,",
  },
  {
    title: "Track 3",
    artist: "Artist 3",
    albumCover:
      "data:image/jpeg;base64,",
  },
];

const MainPage = () => {
  const [currentTrackIndex, setCurrentTrackIndex] = useState(0);
  const [direction, setDirection] = useState(0);
  const controls = useAnimation();
  const dragControls = useDragControls();

  const handleSwipe = (dir) => {
    setDirection(dir);
    if (dir > 0) {
      if (currentTrackIndex > 0) {
        setCurrentTrackIndex(currentTrackIndex - 1);
      }
    } else {
      if (currentTrackIndex < tracks.length - 1) {
        setCurrentTrackIndex(currentTrackIndex + 1);
      }
    }
  };

  const handlePrev = () => {
    setDirection(1);
    if (currentTrackIndex > 0) {
      setCurrentTrackIndex(currentTrackIndex - 1);
    }
  };

  const handleNext = () => {
    setDirection(-1);
    if (currentTrackIndex < tracks.length - 1) {
      setCurrentTrackIndex(currentTrackIndex + 1);
    }
  };

  const variants = {
    enter: (direction) => ({
      x: direction > 0 ? 1000 : -1000,
      opacity: 0,
      rotate: direction > 0 ? 15 : -15,
    }),
    center: {
      zIndex: 1,
      x: 0,
      opacity: 1,
      rotate: 0,
      transition: { type: "spring", stiffness: 300, damping: 30 },
    },
    exit: (direction) => ({
      zIndex: 0,
      x: direction < 0 ? 1000 : -1000,
      opacity: 0,
      rotate: direction < 0 ? 15 : -15,
      transition: { type: "spring", stiffness: 300, damping: 30 },
    }),
  };

  return (
    <MainWrapper>
      <DriveNav />
      <TopBar>
        <GenreButton>장르 선택</GenreButton>
      </TopBar>
      <AlbumWrapper>
        {currentTrackIndex > 0 && (
          <BlurredAlbumCover
            src={tracks[currentTrackIndex - 1].albumCover}
            alt="Previous Album Cover"
            style={{ transform: "rotate(-15deg)", left: "-90px" }}
          />
        )}
        <motion.div
          key={currentTrackIndex}
          custom={direction}
          variants={variants}
          initial="enter"
          animate="center"
          exit="exit"
          drag="x"
          dragConstraints={{ left: 0, right: 0 }}
          dragControls={dragControls}
          onDragEnd={(event, info) => {
            if (info.offset.x < -50) {
              handleSwipe(-1);
            } else if (info.offset.x > 50) {
              handleSwipe(1);
            }
          }}
        >
          <AlbumCover
            src={tracks[currentTrackIndex].albumCover}
            alt="Album Cover"
          />
          <TrackInfo>
            <TrackTitle>{tracks[currentTrackIndex].title}</TrackTitle>
            <TrackArtist>{tracks[currentTrackIndex].artist}</TrackArtist>
            <TrackAlbum>{tracks[currentTrackIndex].album}</TrackAlbum>
          </TrackInfo>
        </motion.div>
        {currentTrackIndex < tracks.length - 1 && (
          <BlurredAlbumCover
            src={tracks[currentTrackIndex + 1].albumCover}
            alt="Next Album Cover"
            style={{ transform: "rotate(15deg)", right: "-90px" }}
          />
        )}
      </AlbumWrapper>
      <ButtonWrapper>
        <NavButton onClick={handlePrev} disabled={currentTrackIndex === 0}>
          ◀
        </NavButton>
        <NavButton
          onClick={handleNext}
          disabled={currentTrackIndex === tracks.length - 1}
        >
          ▶
        </NavButton>
      </ButtonWrapper>
      <MusicPlayer />
      <NowPlaying>
        <NowPlayingHeader>
          <NowPlayingTitle>NOW PLAYING</NowPlayingTitle>
          <MoreInfoLink>앨범 정보 자세히 보기</MoreInfoLink>
        </NowPlayingHeader>
        <NowPlayingTrack>
          <TrackImage
            src={tracks[currentTrackIndex].albumCover}
            alt="Album Cover"
          />
          <TrackDetails>
            <TrackTitle>{tracks[currentTrackIndex].title}</TrackTitle>
            <TrackArtist>{tracks[currentTrackIndex].artist}</TrackArtist>
            <TrackAlbum>{tracks[currentTrackIndex].album}</TrackAlbum>
          </TrackDetails>
        </NowPlayingTrack>
      </NowPlaying>
    </MainWrapper>
  );
};

export default MainPage;

